<script>import { getContext, setContext } from "svelte";
import { writable } from "svelte/store";
import { El } from "../el";
import AccordionHeader from "./AccordionHeader.svelte";
import { get_current_component } from "svelte/internal";
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
export let componentName = "accordion";
export let open = false;
export let title = void 0;
const ctx = writable({ open });
setContext("ACCORDION", ctx);
const parentCtx = getContext("ACCORDIONS");
parentCtx.update((item) => {
  item.children.push(ctx);
  return item;
});
</script>

<El {components} {...$$restProps} {componentName}>
	{#if title}
		<AccordionHeader>
			{title}
		</AccordionHeader>
	{/if}
	<slot />
</El>
