<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "badge";
export let tag = "span";
export let color = void 0;
export let dot = void 0;
export let ghost = void 0;
export let href = void 0;
export let outline = void 0;
export let pill = void 0;
export let tile = void 0;
export let blink = void 0;
export let notification = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let cssProps = {};
let props = {};
$:
  cssProps = {
    notification,
    tile,
    pill,
    outline,
    ghost,
    dot,
    color,
    blink
  };
$:
  props = {
    href,
    tag: href ? "a" : tag,
    componentName
  };
</script>

<El {components} {...$$restProps} {cssProps} {...props} tag={href ? 'a' : tag}>
	{#if !dot}
		<slot />
	{/if}
</El>
