<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let active = void 0;
export let color = void 0;
export let componentName = "button";
export let disabled = void 0;
export let ghost = void 0;
export let href = void 0;
export let link = void 0;
export let loading = void 0;
export let outline = void 0;
export let shape = void 0;
export let size = void 0;
export let target = void 0;
export let type = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let cssProps = {};
let props = {};
let element;
$:
  icon = !(element?.textContent ?? true);
$: {
  cssProps = {
    active,
    color,
    disabled,
    ghost,
    icon,
    link,
    loading,
    outline,
    shape,
    size
  };
  props = {
    componentName,
    href,
    role: "button",
    tag: href ? "a" : "button",
    target,
    type: type ?? "button"
  };
}
</script>

<El {components} bind:element {...$$restProps} {cssProps} {...props} on:click>
	<slot />
</El>
