<script>import { El } from "../el";
import CardHeader from "./CardHeader.svelte";
import CardTitle from "./CardTitle.svelte";
import { get_current_component } from "svelte/internal";
export let componentName = "card";
export let title = void 0;
export let stacked = void 0;
export let statusColor = "primary";
export let statusPosition = "top";
export let statusSize = "md";
export let status = void 0;
export let bgColor = void 0;
export let borderless = void 0;
export let rotate = void 0;
export let state = void 0;
export let size = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let cssProps = {};
let props = {};
let statusCssProps = {};
$: {
  cssProps = {
    stacked,
    bgColor,
    borderless,
    rotate,
    state,
    size
  };
  props = {
    componentName
  };
  statusCssProps = {
    size: statusSize,
    position: statusPosition,
    color: statusColor
  };
}
</script>

<El {components} {...$$restProps} {cssProps} {...props}>
	{#if status}
		<El componentName={componentName + '-status'} cssProps={statusCssProps} />
	{/if}
	{#if title}
		<CardHeader><CardTitle>{title}</CardTitle></CardHeader>
	{/if}
	<slot />
</El>
