<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "checkbox";
export let color = void 0;
export let checked = false;
export let description = void 0;
export let disabled = void 0;
export let indeterminate = false;
export let inline = false;
export let label = void 0;
export let name = void 0;
export let reverse = void 0;
export let value = void 0;
export let id = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let element;
let checkboxProps;
let labelProps;
$:
  wrapperProps = {
    componentName: componentName + "-wrapper",
    cssProps: {
      inline,
      reverse
    }
  };
$:
  checkboxProps = {
    ...$$restProps,
    componentName,
    disabled,
    checked,
    value,
    name,
    type: "checkbox",
    tag: "input",
    cssProps: {
      color,
      indeterminate
    }
  };
$:
  labelProps = {
    componentName: componentName + "-label",
    tag: "label",
    for: id
  };
$:
  descriptionProps = {
    componentName: componentName + "-description"
  };
const onChange = (event) => {
  checked = event.target.checked;
};
</script>

<El {...wrapperProps}>
	<El {...checkboxProps} bind:id bind:element on:change={onChange} {components} />
	{#if label || $$slots['default']}
		<El {...labelProps}>
			<slot>
				{label}
			</slot>
		</El>
	{/if}

	{#if description || $$slots['description']}
		<El {...descriptionProps}>
			<slot name="description">
				{description}
			</slot>
		</El>
	{/if}
</El>
