<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let animated = void 0;
export let color = void 0;
export let componentName = "dot";
export let indicator = void 0;
export let tag = "span";
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let cssProps = {};
let props = {};
$: {
  cssProps = {
    animated,
    color,
    indicator
  };
  props = {
    tag,
    componentName
  };
}
</script>

<El {components} {...$$restProps} {cssProps} {...props}>
	{#if indicator}
		<El componentName="dot-indicator-circle" />
		<El componentName="dot-indicator-circle" />
		<El componentName="dot-indicator-circle" />
	{/if}
</El>
