<script>import { get_current_component } from "svelte/internal";
import { setContext } from "svelte";
import { writable } from "svelte/store";
import { El } from "../el";
export let componentName = "dropdown";
export let placement = "bottom";
export let arrow = true;
export let autoClose = "outside";
let targetEl;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
const context = writable({});
setContext("DROPDOWN", context);
$:
  $context = {
    target: targetEl?.firstElementChild ?? void 0,
    placement,
    autoClose
  };
$:
  cssProps = {
    placement: placement?.split("-")[0],
    arrow
  };
</script>

<El {components} {...$$restProps} {componentName} {cssProps}>
	<El componentName="{componentName}-target" bind:element={targetEl}>
		<slot name="target" />
	</El>
	<slot />
</El>
