<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "dropdown-item";
export let divider = void 0;
export let header = void 0;
export let active = void 0;
export let disabled = void 0;
export let href = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
$:
  cssProps = {
    active,
    disabled,
    header,
    divider
  };
let wrapperProps;
$:
  wrapperProps = {
    componentName: componentName + "-wrapper",
    tag: "li"
  };
let props;
$:
  props = {
    componentName,
    href,
    tag: "a"
  };
</script>

{#if divider}
	<El {components} tag="hr" componentName="{componentName}-divider" />
{:else if header}
	<El {components} tag="h6" componentName="{componentName}-header">
		<slot />
	</El>
{:else}
	<El {components} {...$$restProps} {...wrapperProps}>
		<El {...props} {cssProps} on:click>
			<slot />
		</El>
	</El>
{/if}
