export let mx = void 0;
export let my = void 0;
export let clearfix = void 0;
export let ratio = void 0;
export let sticky = void 0;
export let fixed = void 0;
export let d = void 0;
export let dSm = void 0;
export let dMd = void 0;
export let dLg = void 0;
export let dXl = void 0;
export let dXxl = void 0;
export let dPrint = void 0;
export let w = void 0;
export let h = void 0;
export let mw = void 0;
export let mh = void 0;
export let position = void 0;
export let top = void 0;
export let start = void 0;
export let bottom = void 0;
export let end = void 0;
export let textColor = void 0;
export let textAlign = void 0;
export let textAlignSm = void 0;
export let textAlignMd = void 0;
export let textAlignLg = void 0;
export let textAlignXl = void 0;
export let textWrap = void 0;
export let textTransform = void 0;
export let textDecoration = void 0;
export let lineHeight = void 0;
export let textMuted = void 0;
export let textLead = void 0;
export let textHeading = void 0;
export let textTruncate = void 0;
export let textOpacity = void 0;
export let fontSize = void 0;
export let fontWeight = void 0;
export let fontStyle = void 0;
export let float = void 0;
export let floatSm = void 0;
export let floatMd = void 0;
export let floatLg = void 0;
export let floatXl = void 0;
export let floatXxl = void 0;
export let container = void 0;
export let col = void 0;
export let colSm = void 0;
export let colMd = void 0;
export let colLg = void 0;
export let colXl = void 0;
export let colXxl = void 0;
export let order = void 0;
export let orderSm = void 0;
export let orderMd = void 0;
export let orderLg = void 0;
