export let rowColsMd = void 0;
export let rowColsLg = void 0;
export let rowColsXl = void 0;
export let rowColsXxl = void 0;
export let g = void 0;
export let gSm = void 0;
export let gMd = void 0;
export let gLg = void 0;
export let gXl = void 0;
export let gXxl = void 0;
export let gx = void 0;
export let gy = void 0;
export let alignItems = void 0;
export let alignSelf = void 0;
export let justifyContent = void 0;
const myForwardEvents = forwardEventsBuilder(get_current_component());
const forwardEvents0 = components?.[0] ? forwardEventsBuilder(components[0].component, components[0].except) : noop;
const forwardEvents1 = components?.[1] ? forwardEventsBuilder(components[1].component, components[1].except) : noop;
const forwardEvents2 = components?.[2] ? forwardEventsBuilder(components[2].component, components[2].except) : noop;
const forwardEvents3 = components?.[3] ? forwardEventsBuilder(components[3].component, components[3].except) : noop;
const forwardEvents4 = components?.[4] ? forwardEventsBuilder(components[4].component, components[4].except) : noop;
$: {
  if (animate && show === false) {
    animate.leave();
  }
  if (animate && show === true) {
    animate.enter();
  }
}
$: {
  defaultCssProps = {
    // background properties
    bgColor,
    bgGradient,
    bgOpacity,
    // border properties
    border,
    borderTop,
    borderStart,
    borderEnd,
    borderBottom,
    borderColor,
    borderRadius,
    borderRoundSize,
    borderOpacity,
    // padding properties
