    mh,
    // position
    position,
    top,
    start,
    bottom,
    end,
    // vertical align
    vAlign,
    // text
    textColor,
    textAlign,
    textAlignSm,
    textAlignMd,
    textAlignLg,
    textAlignXl,
    textWrap,
    textTransform,
    textDecoration,
    lineHeight,
    textMuted,
    textLead,
    textHeading,
    textTruncate,
    textOpacity,
    // fonts
    fontSize,
    fontWeight,
    fontStyle,
    hidden,
    float,
    floatSm,
    floatMd,
    floatLg,
    floatXl,
    floatXxl,
    // container
    container,
    // col layout
    col,
    colSm,
    colMd,
    colLg,
    colXl,
    colXxl,
    // Col Order
    order,
    orderSm,
    orderMd,
    orderLg,
    orderXl,
    orderXxl,
    // Col Offset
    offset,
    offsetSm,
    offsetMd,
    offsetLg,
    offsetXl,
    offsetXxl,
    //Row
    row,
    rowCols,
    rowColsSm,
    rowColsMd,
    rowColsLg,
    rowColsXl,
    rowColsXxl,
    // gutter
    g,
    gSm,
    gMd,
    gLg,
    gXl,
    gXxl,
    gx,
    gy,
    // align items
    alignItems,
    alignSelf,
    justifyContent
  };
  classes = classname(elComponentName, defaultCssProps, $$props.class);
  if (componentName !== elComponentName)
    classes += " " + classname(componentName, cssProps);
  classes += $animate?.classes || "";
  const styles = `${style || ""};${$animate?.styles || ""}`;
  elProps = {
    id,
    class: classes,
    title,
    tabindex,
    role,
    ariaCurrent,
    ariaLabel,
    ariaValuenow,
    style: styles
  };
}
onMount(() => {
  if (element) {
    animate = createAnimationStore({
      element,
      componentName,
      show
    });
  }
});
</script>

{#if $$slots.default}
	<svelte:element
		this={tag}
		use:forwardEvents0
		use:forwardEvents1
		use:forwardEvents2
		use:forwardEvents3
		use:forwardEvents4
		use:myForwardEvents
		bind:this={element}
		{...$$restProps}
		{...elProps}>
		<slot />
	</svelte:element>
{:else if tag === 'input'}
	<input
		use:forwardEvents0
		use:forwardEvents1
		use:forwardEvents2
		use:forwardEvents3
		use:forwardEvents4
		use:myForwardEvents
		bind:this={element}
		{...$$restProps}
		{...elProps}
		bind:value />
{:else if tag === 'textarea'}
	<textarea
		use:forwardEvents0
		use:forwardEvents1
		use:forwardEvents2
		use:forwardEvents3
		use:forwardEvents4
		use:myForwardEvents
		bind:this={element}
		{...$$restProps}
		{...elProps}
		bind:value />
{:else}
	<svelte:element
		this={tag}
		use:forwardEvents0
		use:forwardEvents1
		use:forwardEvents2
		use:forwardEvents3
		use:forwardEvents4
		use:myForwardEvents
		bind:this={element}
		{...$$restProps}
		{...elProps} />
{/if}
