<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "input";
export let disabled = void 0;
export let placeholder = void 0;
export let accept = void 0;
export let name = void 0;
export let multiple = void 0;
export let required = void 0;
export let size = void 0;
export let state = void 0;
export let files = void 0;
export let id = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let element;
let props = { componentName, placeholder, disabled };
let cssProps = { state };
let wrapperCssProps = { size };
const onChange = (e) => {
  files = e.target.files;
};
$: {
  cssProps = {
    size,
    state
  };
  props = {
    componentName,
    placeholder,
    disabled,
    accept,
    name,
    multiple,
    required,
    tag: "input",
    type: "file"
  };
}
</script>

{#if $$slots.start || $$slots.end}
	<El componentName="{componentName}-wrapper" {...$$restProps} cssProps={wrapperCssProps}>
		{#if $$slots.start}
			<El tag="span" componentName="{componentName}-icon">
				<slot name="start" />
			</El>
		{/if}
		<El {components} bind:id bind:element {...props} {cssProps} on:change={onChange} />
		{#if $$slots.end}
			<El tag="span" componentName="{componentName}-icon">
				<slot name="end" />
			</El>
		{/if}
	</El>
{:else}
	<El {...$$restProps} {components} bind:id bind:element {...props} {cssProps} on:change={onChange} />
{/if}
