<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "fieldset";
export let legend = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let cssProps = {};
let props = {};
$: {
  cssProps = {};
  props = {
    componentName,
    tag: "fieldset"
  };
}
</script>

<El {components} {...$$restProps} {cssProps} {...props}>
	{#if $$slots['legend'] || legend}
		<El tag="legend" componentName="{componentName}-legend">
			<slot name="legend">
				{#if legend}
					{legend}
				{/if}
			</slot>
		</El>
	{/if}
	<slot />
</El>
