<script>import { get_current_component } from "svelte/internal";
import { Autocomplete } from "../autocomplete";
import FormField from "./FormField.svelte";
import { El } from "../el";
export let componentName = "form-autocomplete";
export let disabled = void 0;
export let hint = void 0;
export let items = [];
export let key = void 0;
export let label = void 0;
export let name = void 0;
export let placeholder = void 0;
export let required = void 0;
export let multiple = void 0;
export let dismissible = void 0;
export let size = void 0;
export let state = void 0;
export let value = void 0;
export let create = void 0;
const components = [
  { component: get_current_component(), except: ["input", "changed", "created"] },
  ...$$props.components ?? []
];
let id;
let props = {};
let autocompleteProps = {};
$: {
  props = {
    required,
    label,
    hint,
    state,
    id
  };
  autocompleteProps = {
    disabled,
    items,
    key,
    placeholder,
    multiple,
    dismissible,
    name,
    size,
    state,
    create
  };
}
</script>

<FormField {componentName} {...props} {...$$restProps}>
	<slot name="label" slot="label" />
	<svelte:fragment slot="group">
		<slot name="start">
			{#if $$slots['start-icon']}
				<El componentName="{componentName}-icon">
					<slot name="start-icon" />
				</El>
			{/if}
		</slot>
		<Autocomplete
			{...autocompleteProps}
			on:changed
			on:created
			on:input
			{components}
			_slots={{ default: $$slots['default'], selected: $$slots['selected'] }}
			bind:value
			bind:id
			let:item
			let:index>
			<slot {index} {item}>{item}</slot>
			<slot name="selected" slot="selected" {index} {item}>{item}</slot>
		</Autocomplete>

		<slot name="end">
			{#if $$slots['end-icon']}
				<El componentName="{componentName}-icon">
					<slot name="end-icon" />
				</El>
			{/if}
		</slot>
	</svelte:fragment>
	<slot name="hint" slot="hint" />
</FormField>
