<script>import { get_current_component } from "svelte/internal";
import { Checkbox } from "../checkbox";
import FormField from "./FormField.svelte";
export let label = void 0;
export let hint = void 0;
export let state = void 0;
export let required = void 0;
export let componentName = "form-checkbox";
export let color = void 0;
export let disabled = void 0;
export let description = void 0;
export let reverse = void 0;
export let value = void 0;
export let checked = void 0;
export let indeterminate = void 0;
export let inline = void 0;
export let name = void 0;
let id;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
$:
  props = {
    required,
    label,
    hint,
    state,
    id,
    componentName
  };
$:
  checkboxProps = {
    color,
    inline,
    description,
    reverse,
    indeterminate,
    disabled,
    name
  };
</script>

<FormField {...props} {...$$restProps}>
	<slot name="label" slot="label" />
	<Checkbox bind:id {...checkboxProps} bind:checked bind:value {components}>
		<slot />
	</Checkbox>
	<slot name="hint" slot="hint" />
</FormField>
