<script>import { get_current_component } from "svelte/internal";
import DatePicker from "../date-picker/DatePicker.svelte";
import FormField from "./FormField.svelte";
import { El } from "../el";
export let componentName = "form-date-picker";
export let disabled = void 0;
export let borderRounded = void 0;
export let borderFlush = void 0;
export let placeholder = void 0;
export let range = void 0;
export let required = void 0;
export let options = void 0;
export let size = void 0;
export let state = void 0;
export let value = void 0;
export let label = void 0;
export let hint = void 0;
export let name = void 0;
const components = [
  { component: get_current_component(), except: ["changed"] },
  ...$$props.components ?? []
];
let id;
let props = {};
let datePickerProps = {};
$: {
  props = {
    componentName,
    required,
    label,
    hint,
    state,
    id
  };
  datePickerProps = {
    placeholder,
    disabled,
    required,
    options,
    size,
    state,
    range,
    borderRounded,
    borderFlush,
    name
  };
}
</script>

<FormField {...props} {...$$restProps}>
	<slot name="label" slot="label" />

	<svelte:fragment slot="group">
		<slot name="start">
			{#if $$slots['start-icon']}
				<El componentName="{componentName}-icon">
					<slot name="start-icon" />
				</El>
			{/if}
		</slot>
		<DatePicker bind:id {components} {...datePickerProps} bind:value on:changed />
		<slot name="end">
			{#if $$slots['end-icon']}
				<El componentName="{componentName}-icon">
					<slot name="end-icon" />
				</El>
			{/if}
		</slot>
	</svelte:fragment>
	<slot name="hint" slot="hint" />
</FormField>
