<script>import FormField from "./FormField.svelte";
import { Editor } from "../editor";
import { get_current_component } from "svelte/internal";
export let componentName = "form-editor";
export let placeholder = void 0;
export let readonly = void 0;
export let required = void 0;
export let toolbar = void 0;
export let height = void 0;
export let name = void 0;
export let state = void 0;
export let value = void 0;
export let label = void 0;
export let hint = void 0;
const components = [
  { component: get_current_component(), except: ["changed"] },
  ...$$props.components ?? []
];
let id;
let props = {};
let editorProps = {};
$: {
  props = {
    required,
    label,
    hint,
    state,
    componentName
  };
  editorProps = {
    placeholder,
    readonly,
    required,
    toolbar,
    height,
    state,
    name
  };
}
</script>

<FormField {id} {...props} {...$$restProps}>
	<slot name="label" slot="label" />
	<Editor bind:id {components} {...editorProps} on:changed bind:value />
	<slot name="hint" slot="hint" />
</FormField>
