<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
import { Label } from "../label";
export let componentName = "form-field";
export let label = void 0;
export let id = void 0;
export let hint = void 0;
export let required = void 0;
export let state = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
</script>

<El {components} {...$$restProps} {componentName}>
	{#if label}
		<Label for={id} {required}>{label}</Label>
	{/if}
	<slot name="label" />
	<slot>
		<El componentName="{componentName}-group">
			<slot name="group" />
		</El>
	</slot>
	<slot name="hint" />
	{#if hint}
		<El componentName={componentName + '-hint'} cssProps={{ state }}>
			{hint}
		</El>
	{/if}
</El>
