<script>import { get_current_component } from "svelte/internal";
import { Input } from "../input";
import FormField from "./FormField.svelte";
import { El } from "../el";
export let componentName = "form-input";
export let disabled = void 0;
export let borderRounded = void 0;
export let borderFlush = void 0;
export let mask = void 0;
export let maskOptions = void 0;
export let placeholder = void 0;
export let readonly = void 0;
export let required = void 0;
export let size = void 0;
export let state = void 0;
export let type = void 0;
export let value = void 0;
export let label = void 0;
export let hint = void 0;
export let name = void 0;
export let min = void 0;
export let max = void 0;
export let minlength = void 0;
export let maxlength = void 0;
export let pattern = void 0;
export let step = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let id;
let props = {};
let inputProps = {};
$:
  props = {
    required,
    label,
    hint,
    state,
    componentName,
    id
  };
$:
  inputProps = {
    placeholder,
    disabled,
    readonly,
    type,
    required,
    size,
    state,
    borderRounded,
    borderFlush,
    mask,
    maskOptions,
    name,
    min,
    max,
    minlength,
    maxlength,
    pattern,
    step
  };
</script>

<FormField {...props} {...$$restProps}>
	<slot slot="label" name="label" />
	<svelte:fragment slot="group">
		<slot name="start">
			{#if $$slots['start-icon']}
				<El componentName="{componentName}-icon">
					<slot name="start-icon" />
				</El>
			{/if}
		</slot>
		<Input bind:id {components} {...inputProps} bind:value />
	
		<slot name="end">
			{#if $$slots['end-icon']}
				<El componentName="{componentName}-icon">
					<slot name="end-icon" />
				</El>
			{/if}
		</slot>
	</svelte:fragment>
	<slot slot="hint" name="hint" />
</FormField>
