<script>import { get_current_component } from "svelte/internal";
import FormField from "./FormField.svelte";
import RadioGroup from "../radio/RadioGroup.svelte";
export let color = void 0;
export let inline = void 0;
export let items = void 0;
export let key = void 0;
export let reverse = void 0;
export let value = void 0;
export let name = void 0;
export let componentName = "form-radio-group";
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let radioGroupProps = {};
$: {
  radioGroupProps = {
    color,
    inline,
    reverse,
    items,
    name,
    key
  };
}
</script>

<FormField {...$$restProps} {componentName}>
	<slot name="label" slot="label" />
	<RadioGroup {components} {...radioGroupProps} bind:value let:item let:index>
		<slot {index} {item}>{item}</slot>
	</RadioGroup>
	<slot name="hint" slot="hint" />
</FormField>
