<script>import { get_current_component } from "svelte/internal";
import FormField from "./FormField.svelte";
import { Select } from "../select";
import { El } from "../el";
export let items = void 0;
export let value = void 0;
export let name = void 0;
export let size = void 0;
export let disabled = void 0;
export let label = void 0;
export let hint = void 0;
export let required = void 0;
export let multiple = void 0;
export let placeholder = void 0;
export let state = void 0;
export let key = void 0;
export let componentName = "form-select";
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let selectProps = {};
let props = {};
let id;
$: {
  selectProps = {
    placeholder,
    disabled,
    multiple,
    size,
    items,
    state,
    key,
    name
  };
  props = {
    componentName,
    required,
    label,
    hint,
    state,
    id
  };
}
</script>

<FormField {...props} {...$$restProps}>
	<slot name="label" slot="label" />
	<svelte:fragment slot="group">
		<slot name="start">
			{#if $$slots['start-icon']}
				<El componentName="{componentName}-icon">
					<slot name="start-icon" />
				</El>
			{/if}
		</slot>
		<Select {components} {...selectProps} bind:value bind:id let:item let:index>
			<slot {index} {item}>{item}</slot>
		</Select>

		<slot name="end">
			{#if $$slots['end-icon']}
				<El componentName="{componentName}-icon">
					<slot name="end-icon" />
				</El>
			{/if}
		</slot>
	</svelte:fragment>
	<slot name="hint" slot="hint" />
</FormField>
