<script>import { Slider } from "../slider";
import FormField from "./FormField.svelte";
import { get_current_component } from "svelte/internal";
export let componentName = "form-input";
export let tag = "input";
export let required = void 0;
export let color = void 0;
export let connect = void 0;
export let min = void 0;
export let max = void 0;
export let step = void 0;
export let state = void 0;
export let label = void 0;
export let hint = void 0;
export let name = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let props = {};
let sliderProps = {};
$:
  props = {
    required,
    label,
    hint,
    state,
    componentName
  };
$:
  sliderProps = {
    tag,
    required,
    state,
    color,
    connect,
    min,
    max,
    step,
    name
  };
</script>

<FormField {...props} {...$$restProps}>
	<slot name="label" slot="label" />
	<Slider {components} {...sliderProps}>
		<slot />
	</Slider>
	<slot name="hint" slot="hint" />
</FormField>
