<script>import FormField from "./FormField.svelte";
import { Switch } from "../switch";
import { get_current_component } from "svelte/internal";
export let label = void 0;
export let hint = void 0;
export let name = void 0;
export let state = void 0;
export let required = void 0;
export let componentName = "form-switch";
export let color = void 0;
export let disabled = void 0;
export let description = void 0;
export let reverse = void 0;
export let value = void 0;
export let checked = void 0;
export let inline = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let id;
let props = {};
let switchProps = {};
$: {
  props = {
    required,
    label,
    hint,
    state,
    id,
    componentName
  };
  switchProps = {
    color,
    inline,
    description,
    reverse,
    checked,
    disabled,
    name
  };
}
</script>

<FormField {...props} {...$$restProps}>
	<slot name="label" slot="label" />
	<Switch
		bind:id
		{...switchProps}
		bind:checked
		bind:value
		{components}
		_slots={{ default: $$slots['default'], description: $$slots['description'] }}>
		<slot />
		<slot name="description" slot="description" />
	</Switch>
	<slot name="hint" slot="hint" />
</FormField>
