<script>import FormField from "./FormField.svelte";
import { Textarea } from "../textarea";
import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "form-textarea";
export let tag = "textarea";
export let disabled = void 0;
export let borderRounded = void 0;
export let borderFlush = void 0;
export let placeholder = void 0;
export let readonly = void 0;
export let required = void 0;
export let rows = 3;
export let name = void 0;
export let size = void 0;
export let state = void 0;
export let type = void 0;
export let value = void 0;
export let label = void 0;
export let hint = void 0;
export let minlength = void 0;
export let maxlength = void 0;
export let cols = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let id;
let props = {};
let teaxtareaProps = {};
$: {
  props = {
    required,
    label,
    hint,
    state,
    componentName
  };
  teaxtareaProps = {
    tag,
    placeholder,
    disabled,
    readonly,
    type,
    required,
    size,
    state,
    borderRounded,
    borderFlush,
    rows,
    name,
    minlength,
    maxlength,
    cols
  };
}
</script>

<FormField {id} {...props} {...$$restProps}>
	<slot name="label" slot="label" />
	<svelte:fragment slot="group">
		<slot name="start">
			{#if $$slots['start-icon']}
				<El componentName="{componentName}-icon">
					<slot name="start-icon" />
				</El>
			{/if}
		</slot>
		<Textarea bind:id {components} {...teaxtareaProps} bind:value />
		<slot name="end">
			{#if $$slots['end-icon']}
				<El componentName="{componentName}-icon">
					<slot name="end-icon" />
				</El>
			{/if}
		</slot>
	</svelte:fragment>
	<slot name="hint" slot="hint" />
</FormField>
