<script context="module">
	import { appendCustomStyle } from 'iconify-icon'

	appendCustomStyle(`svg [stroke-width="2"] { stroke-width: 1.5; }`)
</script>

<script>import "iconify-icon";
import { onMount } from "svelte";
import { El } from "../el";
import { get_current_component } from "svelte/internal";
export let componentName = "icon";
export let color = void 0;
export let name = void 0;
export let pack = "tabler";
export let size = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let loaded = false;
onMount(() => {
  loaded = true;
});
$:
  cssProps = {
    color,
    size
  };
</script>

<El {componentName} {components} {...$$restProps} {cssProps}>
	{#if loaded}
		<iconify-icon icon="{pack}:{name}" width="100%" height="100%" />
	{/if}
</El>
