<script>import { onMount, setContext, createEventDispatcher } from "svelte";
import { El } from "../el";
import { get_current_component } from "svelte/internal";
export let componentName = "offcanvas";
export let placement = "start";
export let noScroll = void 0;
export let backdrop = void 0;
export let autoClose = void 0;
export let show = void 0;
const dispatch = createEventDispatcher();
const components = [
  { component: get_current_component(), except: ["close"] },
  ...$$props.components ?? []
];
const close = () => {
  show = false;
  dispatch("close");
};
setContext("OFFCANVAS", { close });
let element;
let instance;
let props = { componentName, ...$$restProps };
let cssProps = { placement };
const handleEscapeKey = (event) => {
  if (show && element && autoClose && event.key === "Escape" && !event.defaultPrevented) {
    close();
  }
};
const handleOutsideClick = (event) => {
  if (show && element && autoClose && !element.contains(event.target) && !event.defaultPrevented) {
    close();
  }
};
onMount(() => {
  import("focus-trap").then((focusTrap) => {
    instance = focusTrap.createFocusTrap(element, {
      escapeDeactivates: false
    });
  });
  if (document && autoClose) {
    document.addEventListener("keyup", handleEscapeKey, true);
    document.addEventListener("click", handleOutsideClick, true);
    return () => {
      document.removeEventListener("keyup", handleEscapeKey, true);
      document.removeEventListener("click", handleOutsideClick, true);
    };
  }
});
$:
  if (instance) {
    setTimeout(() => {
      try {
        if (show) {
          instance.activate();
        } else {
          instance.deactivate();
        }
      } catch (err) {
      }
    }, 500);
  }
$: {
  cssProps = { placement };
}
</script>

<El {components} componentName="{componentName}-wrapper">
	<El {...props} {...$$restProps} {cssProps} {componentName} bind:element tabindex="0" {show}>
		<slot />
	</El>
	{#if backdrop}
		<El componentName="{componentName}-backdrop" on:click={handleOutsideClick} {show} />
	{/if}
	{#if show}
		{#if noScroll}
			<El componentName="{componentName}-no-scroll" />
		{/if}
	{/if}
</El>
