<script>import { getContext } from "svelte";
import { El } from "../el";
import { Button } from "../button";
import { get_current_component } from "svelte/internal";
export let componentName = "offcanvas-header";
export let title = void 0;
const ctx = getContext("OFFCANVAS");
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
const onClick = () => {
  if (ctx && ctx.close)
    ctx.close();
};
</script>

<El {...$$restProps} {componentName} {components}>
	{#if title}
		<El componentName="{componentName}-title">{title}</El>
	{:else}
		<slot />
	{/if}
	<Button componentName="{componentName}-btn" aria-label="Close" on:click={onClick} />
</El>
