<script>import { createEventDispatcher } from "svelte";
import { El } from "../el";
import { get_current_component } from "svelte/internal";
export let componentName = "pagination-item";
export let href = void 0;
export let active = void 0;
export let disabled = void 0;
const dispatch = createEventDispatcher();
const components = [
  { component: get_current_component(), except: ["click"] },
  ...$$props.components ?? []
];
function onClick() {
  if (disabled)
    return;
  dispatch("click");
}
$:
  cssProps = {
    active,
    disabled
  };
let linkProps;
$:
  linkProps = {
    tag: "a",
    href,
    componentName: componentName + "-link"
  };
</script>

<El {...$$restProps} {componentName} {components} {cssProps} on:click={onClick}>
	<El {...linkProps}>
		<slot />
	</El>
</El>
