<script>import { El } from "../el";
import { get_current_component } from "svelte/internal";
import { classname } from "../internal";
import {
  computePosition,
  offset as offsetMiddleware,
  shift as shiftMiddleware,
  flip as flipMiddleware,
  arrow as arrowMiddleware
} from "@floating-ui/dom";
export let target = void 0;
export let placement = void 0;
export let trigger = void 0;
export let popupOffset = void 0;
export let autoClose = "outside";
export let arrow = void 0;
export let componentName = "popup";
export let show = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let popupEl;
let targetEl;
let arrowEl;
let arrowPlacement = void 0;
let arrowX = void 0;
let arrowY = void 0;
let prevTarget;
let timer;
let left = "";
let top = "";
function showPopup() {
  if (timer && trigger === "hover")
    clearTimeout(timer);
  show = true;
  let middleware = [];
  if (popupOffset) {
    middleware.push(offsetMiddleware(popupOffset));
  }
  middleware.push(
    shiftMiddleware({
      padding: popupOffset
    })
  );
  middleware.push(flipMiddleware());
  if (arrow) {
    middleware.push(
      arrowMiddleware({
        element: arrowEl
      })
    );
  }
  computePosition(targetEl, popupEl, {
    placement,
    middleware
  }).then(({ x, y, ...rest }) => {
    left = `${x}px`;
    top = `${y}px`;
    if (arrow) {
      arrowPlacement = rest.placement.split("-")[0];
      const arrowData = rest.middlewareData.arrow;
      if (arrowData) {
        arrowX = arrowData.x ? `${arrowData.x}px` : "";
        arrowY = arrowData.y ? `${arrowData.y}px` : "";
      }
    }
  });
}
function hidePopup() {
  if (trigger === "hover") {
    timer = setTimeout(() => {
      show = false;
    }, 300);
  } else {
    show = false;
  }
}
const togglePopup = () => {
  show ? hidePopup() : showPopup();
};
function onOutside(event) {
  if (!show)
    return;
  if (autoClose === true || autoClose === "outside") {
    if (event.composedPath().some((path) => path == popupEl || path == targetEl)) {
      return;
    }
    show = false;
  }
}
function onClickPopup() {
  if (autoClose === true || autoClose === "inside") {
    show = false;
  }
}
function getTargetEl(target2) {
  if (!target2) {
    return popupEl.previousElementSibling || popupEl.parentElement;
  } else if (typeof target2 === "string") {
    return document.querySelector(target2) || popupEl.parentElement;
  } else {
    return target2;
  }
}
async function bind() {
  if (window) {
    window.process = { env: { NODE_ENV: "production" } };
  }
  prevTarget = targetEl;
  if (typeof window !== "undefined")
    document.addEventListener("click", onOutside);
  popupEl?.addEventListener("click", onClickPopup);
  if (trigger === "click")
    targetEl?.addEventListener("click", togglePopup);
  if (trigger === "hover") {
    targetEl?.addEventListener("mouseenter", showPopup);
    targetEl?.addEventListener("mouseleave", hidePopup);
    popupEl?.addEventListener("mouseenter", showPopup);
    popupEl?.addEventListener("mouseleave", hidePopup);
  }
  if (trigger === "focus") {
    targetEl?.addEventListener("focus", showPopup);
    targetEl?.addEventListener("blur", hidePopup);
  }
}
function unbind() {
  if (prevTarget) {
    if (trigger === "click")
      prevTarget.removeEventListener("click", togglePopup);
    if (typeof window !== "undefined")
      document.removeEventListener("click", onOutside);
    popupEl?.removeEventListener("click", onClickPopup);
    if (trigger === "hover") {
      prevTarget.removeEventListener("mouseenter", showPopup);
      prevTarget.removeEventListener("mouseleave", hidePopup);
      popupEl?.removeEventListener("mouseenter", showPopup);
      popupEl?.removeEventListener("mouseleave", hidePopup);
    }
    if (trigger === "focus") {
      prevTarget.removeEventListener("focus", showPopup);
      prevTarget.removeEventListener("blur", hidePopup);
    }
  }
}
$:
  if (popupEl)
    targetEl = getTargetEl(target);
$:
  if (targetEl) {
    unbind();
    bind();
  }
$:
  popupProps = {
    componentName,
    class: classname(
      "popup",
      {
        show,
        arrowPlacement
      },
      $$props.class
    ),
    style: `left: ${left}; top: ${top};`
  };
$:
  arrowProps = {
    componentName: componentName + "-arrow",
    class: classname("popup-arrow"),
    style: `left: ${arrowX}; top: ${arrowY}`
  };
</script>

<El {components} bind:element={popupEl} {...$$restProps} {...popupProps}>
	{#if arrow}
		<El bind:element={arrowEl} {...arrowProps} />
	{/if}
	<slot />
</El>
