<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "progress-bar";
export let color = void 0;
export let value = void 0;
export let striped = void 0;
export let label = void 0;
export let indeterminate = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let props = {};
let cssProps = {};
let _label = "";
$: {
  cssProps = {
    color,
    striped,
    indeterminate
  };
  props = {
    componentName
  };
  _label = label ?? value + "% completed";
}
</script>

<El
	{...$$restProps}
	{...props}
	{components}
	{cssProps}
	{componentName}
	role="progressbar"
	style="width: {value}%"
	aria-valuenow={+(value || 0)}
	aria-label={_label}>
	{#if label}
		<El tag="span">{_label}</El>
	{:else}
		<El tag="span" componentName="{componentName}-visually-hidden">{value}% Complete</El>
	{/if}
</El>
