<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let color = void 0;
export let description = void 0;
export let componentName = "radio";
export let disabled = void 0;
export let inline = void 0;
export let reverse = void 0;
export let label = void 0;
export let name = void 0;
export let readonly = void 0;
export let checked = false;
export let value = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let labelForId = void 0;
export { labelForId as for };
let inputElement;
let cssProps = {};
let otherProps = {};
$: {
  labelForId = inputElement?.id ?? "";
  cssProps = { color };
  otherProps = {
    componentName,
    disabled,
    readonly,
    value,
    checked,
    name: name ?? inputElement?.id
  };
}
</script>

<El componentName="{componentName}-wrapper" cssProps={{ inline, reverse }}>
	<El
		{components}
		tag="input"
		{...{ type: 'radio' }}
		bind:element={inputElement}
		{...$$restProps}
		{...otherProps}
		{cssProps} />
	{#if label || $$slots['default']}
		<El tag="label" componentName="{componentName}-label" {...{ for: labelForId }}>
			<slot>
				{#if label}
					{label}
				{/if}
			</slot>
		</El>
	{/if}

	{#if description || $$slots['description']}
		<El componentName="{componentName}-description">
			<slot name="description">
				{description}
			</slot>
		</El>
	{/if}
</El>
