<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
import Radio from "./Radio.svelte";
export let color = void 0;
export let componentName = "radio-group";
export let inline = void 0;
export let items = void 0;
export let reverse = void 0;
export let key = void 0;
export let value = void 0;
export let name = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let element;
let props = {};
$: {
  props = {
    inline,
    reverse,
    name: name ?? element?.id,
    color
  };
}
$:
  getKey = (item) => {
    if (key) {
      return typeof key === "string" ? item[key] : key(item);
    } else {
      return item;
    }
  };
const onChange = (event) => {
  value = event.target.value;
};
</script>

<El {componentName} {components} bind:element {...$$restProps}>
	{#if items}
		{#each items as item, index (index)}
			<Radio
				{...props}
				value={getKey(item)}
				checked={value === getKey(item)}
				on:change={onChange}
				on:change>
				<slot {index} {item}>{item}</slot>
			</Radio>
		{/each}
	{:else}
		<slot />
	{/if}
</El>
