<script>import { get_current_component } from "svelte/internal";
import { Icon } from "../icon";
import { El } from "../el";
import { Popup } from "../popup";
export let componentName = "sidebar-item";
export let disabled = void 0;
export let active = void 0;
export let icon = void 0;
export let title = void 0;
export let href = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
let wrapperProps;
$:
  wrapperProps = {
    tag: "li",
    componentName: componentName + "-wrapper"
  };
let props;
$:
  props = {
    tag: href ? "a" : "div",
    href,
    componentName
  };
$:
  dropdown = $$slots["default"];
$:
  cssProps = {
    disabled,
    dropdown,
    active
  };
</script>

<El {components} {...$$restProps} {...wrapperProps} cssProps={{ dropdown }}>
	<El on:click {...props} {cssProps}>
		{#if icon || $$slots['start']}
			<El tag="span" componentName="{componentName}-icon">
				<slot name="start">
					<Icon name={icon} />
				</slot>
			</El>
		{/if}
		{#if title || $$slots['title']}
			<El tag="span" componentName="{componentName}-title">
				<slot name="title">
					{title}
				</slot>
			</El>
		{/if}
		{#if $$slots['end']}
			<El componentName="{componentName}-end">
				<slot name="end" />
			</El>
		{/if}
	</El>

	{#if $$slots['default']}
		<Popup
			tag="ul"
			trigger="click"
			bind:show={active}
			autoClose={false}
			placement="right-start"
			componentName="{componentName}-menu">
			<slot />
		</Popup>
	{/if}
</El>
