<script>import { getContext, onDestroy, onMount } from "svelte";
import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let value = 0;
export let connect = false;
export let tooltip = false;
export let name = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
const { register, unregister, values, setValue } = getContext("SLIDER");
let id = void 0;
onMount(() => {
  const knob = { connect, value, tooltip };
  id = register(knob);
});
onDestroy(() => {
  if (!id)
    return;
  unregister(id);
});
const isDefined = (value2) => typeof value2 !== "undefined";
$:
  if (isDefined(id) && isDefined($values[id])) {
    value = $values[id];
  }
$:
  if (isDefined(id) && isDefined(value)) {
    setValue(id, value);
  }
</script>

<El tag="input" type="hidden" {components} {name} {value} />
