<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "spinner";
export let color = void 0;
export let size = void 0;
export let role = "status";
export let animate = "border";
let props = {};
let cssProps = {};
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
$: {
  cssProps = {
    size,
    color,
    animate
  };
  props = {
    componentName,
    role
  };
}
</script>

<El {components} tag="span" {...$$restProps} {cssProps} {...props}>
	<slot />
</El>
