<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
import { Label } from "../label";
export let componentName = "switch";
export let tag = "input";
export let id = void 0;
export let color = void 0;
export let disabled = void 0;
export let description = void 0;
export let inline = void 0;
export let name = void 0;
export let label = void 0;
export let value = false;
export let reverse = false;
export let checked = false;
export let role = "switch";
export let type = "checkbox";
export let _slots = $$slots;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
function onChange(event) {
  checked = event.target.checked;
  value = event.target.checked;
}
let props = {};
let cssProps = {};
let _for = void 0;
$: {
  _for = id;
  cssProps = {
    color
  };
  props = {
    tag,
    checked,
    componentName,
    label,
    role,
    disabled,
    type,
    name
  };
}
</script>

<El componentName="{componentName}-wrapper" cssProps={{ inline, reverse }}>
	<El bind:id {...$$restProps} {cssProps} {...props} on:change={onChange} {components} />
	{#if label || _slots['default']}
		<Label for={_for} componentName="{componentName}-label">
			{#if _slots['default']}
				<slot />
			{:else}
				{label}
			{/if}
		</Label>
	{/if}
	{#if description || _slots['description']}
		<El componentName="{componentName}-description">
			{#if _slots['description']}
				<slot name="description" />
			{:else}
				{description}
			{/if}
		</El>
	{/if}
</El>
