<script>import { El } from "../el";
import { get_current_component } from "svelte/internal";
export let tag = "table";
export let componentName = "table";
export let color = void 0;
export let hover = void 0;
export let striped = void 0;
export let wrap = void 0;
export let responsive = void 0;
export let size = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
$:
  cssProps = {
    color,
    // border,
    hover,
    striped,
    wrap,
    size
  };
$:
  parentCssProps = {
    responsive
  };
$:
  otherProps = {
    tag,
    componentName
  };
</script>

<El {components} componentName="{componentName}-wrapper" cssProps={parentCssProps}>
	<El {...$$restProps} {cssProps} {...otherProps}>
		<slot />
	</El>
</El>
