<script>import { get_current_component } from "svelte/internal";
import { getContext } from "svelte";
import { El } from "../el";
import { TABS } from "./Tabs.svelte";
export let componentName = "tab-item";
export let tag = "li";
export let active = void 0;
export let disabled = void 0;
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
const tab = {};
const { registerTab, selectTab, selectedTab } = getContext(TABS);
let element;
registerTab(tab);
$:
  active ? selectTab(tab) : null;
$:
  icon = !(element?.textContent ?? true);
let cssProps = {};
$: {
  cssProps = {
    disabled,
    icon,
    active: $selectedTab === tab
  };
}
</script>

<El
	{components}
	{...$$restProps}
	{tag}
	{componentName}
	on:click={() => (!disabled ? selectTab(tab) : null)}>
	<El bind:element tag="button" componentName="{componentName}-link" {cssProps}>
		<slot />
	</El>
</El>
