<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "tab-list";
export let tag = "ul";
export let grow = void 0;
let cssProps = {};
let props = {};
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
$: {
  cssProps = {
    grow
  };
  props = {
    tag,
    componentName,
    role: "tablist"
  };
}
</script>

<El {components} {...$$restProps} {...props} {componentName} {cssProps}>
	<slot />
</El>
