<script>import { get_current_component } from "svelte/internal";
import { El } from "../el";
export let componentName = "textarea";
export let disabled = void 0;
export let borderRounded = void 0;
export let borderFlush = void 0;
export let placeholder = void 0;
export let readonly = void 0;
export let rows = void 0;
export let name = void 0;
export let size = void 0;
export let state = void 0;
export let type = void 0;
export let value = void 0;
export let id = void 0;
export let minlength = void 0;
export let maxlength = void 0;
export let cols = void 0;
let props = {};
let cssProps = {};
const components = [
  { component: get_current_component(), except: [] },
  ...$$props.components ?? []
];
$: {
  cssProps = {
    size,
    state,
    borderRounded,
    borderFlush
  };
  props = {
    componentName,
    placeholder,
    disabled,
    readonly,
    type,
    rows,
    name,
    minlength,
    maxlength,
    cols
  };
}
</script>

{#if $$slots.start || $$slots.end}
	<El {components} componentName="{componentName}-wrapper" {...$$restProps} cssProps={{ size }}>
		{#if $$slots.start}
			<El tag="span" componentName="{componentName}-icon">
				<slot name="start" />
			</El>
		{/if}
		<El bind:id tag="textarea" bind:value {...props} {cssProps} />
		{#if $$slots.end}
			<El tag="span" componentName="{componentName}-icon">
				<slot name="end" />
			</El>
		{/if}
	</El>
{:else}
	<El {components} bind:id tag="textarea" bind:value {...$$restProps} {...props} {cssProps} />
{/if}
